mapper.update:
UPDATE
         pet_hospital_entity
   SET
         hos_latitude  = #{hosLatitude}
       , hos_longitude = #{hosLongitude}
       , hospital_addr = #{hospitalAddr}
       , hospital_name = #{hospitalName}
       , hospital_num  = #{hospitalNum}
       , oper_state    = #{operState}
       , sigun_name    = #{sigunName}
 WHERE   hospital_id   = #{hospitalId}
